          </Card>
        </TabsContent>

        <TabsContent value="pantries" className="space-y-6">
          <PantryManagement />
        </TabsContent>

        <TabsContent value="users" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>User Management</CardTitle>
            </CardHeader>
            <CardContent>
              {isLoadingUsers ? (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
                  <p className="text-muted-foreground">Loading users...</p>
                </div>
              ) : users.length === 0 ? (
                <div className="text-center py-8">
                  <p className="text-muted-foreground">No users found.</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {users.map((user) => (
                    <div key={user.id} className="border rounded-lg p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="flex items-center space-x-2">
                            <h3 className="font-medium">
                              {user.firstName || user.lastName 
                                ? `${user.firstName || ''} ${user.lastName || ''}`.trim()
                                : user.email || 'Unnamed User'
                              }
                            </h3>
                            <Badge variant={user.status === 'approved' ? 'default' : user.status === 'pending' ? 'secondary' : 'destructive'}>
                              {user.status}
                            </Badge>
                            <Badge variant={user.role === 'super_admin' ? 'default' : user.role === 'admin' ? 'secondary' : 'outline'}>
                              {user.role}
                            </Badge>
                          </div>
                          {user.email && (
                            <p className="text-sm text-muted-foreground">{user.email}</p>
                          )}
                          {user.createdAt && (
                            <p className="text-xs text-muted-foreground">
                              Joined: {new Date(user.createdAt).toLocaleDateString()}
                            </p>
                          )}
                        </div>
                        <div className="flex space-x-2">
                          <Select
                            onValueChange={(status) => updateUserStatusMutation.mutate({ userId: user.id, status })}
                            defaultValue={user.status || 'pending'}
                            data-testid={`select-status-${user.id}`}
                          >
                            <SelectTrigger className="w-32">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="pending">Pending</SelectItem>
                              <SelectItem value="approved">Approved</SelectItem>
                              <SelectItem value="rejected">Rejected</SelectItem>
                            </SelectContent>
                          </Select>
                          <Select
                            onValueChange={(role) => updateUserRoleMutation.mutate({ userId: user.id, role })}
                            defaultValue={user.role || 'user'}
                            data-testid={`select-role-${user.id}`}
                          >
                            <SelectTrigger className="w-32">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="user">User</SelectItem>
                              <SelectItem value="admin">Admin</SelectItem>
                              <SelectItem value="super_admin">Super Admin</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

// Pantry Management Component
function PantryManagement() {
  const { toast } = useToast();
  const [editingPantry, setEditingPantry] = useState<string | null>(null);
  const [showDeleteAll, setShowDeleteAll] = useState(false);

  // Fetch pantries
  const { data: pantries, isLoading } = useQuery<any[]>({
    queryKey: ['/api/pantries'],
    retry: false,
  });

  // Delete pantry mutation
  const deletePantryMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest('DELETE', `/api/pantries/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/pantries'] });
      toast({
        title: "Pantry Deleted",
        description: "The pantry has been removed successfully.",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Delete Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Delete all pantries mutation
  const deleteAllMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('DELETE', '/api/pantries');
      return await response.json();
    },
    onSuccess: (data: any) => {
      queryClient.invalidateQueries({ queryKey: ['/api/pantries'] });
      setShowDeleteAll(false);
      toast({
        title: "All Pantries Deleted",
        description: `Successfully removed ${data.count} pantries.`,
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Bulk Delete Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Loading Pantries...</CardTitle>
        </CardHeader>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle>Pantry Management</CardTitle>
          <div className="space-x-2">
            <Button
              variant="destructive"
              onClick={() => setShowDeleteAll(true)}
              disabled={!pantries || !Array.isArray(pantries) || pantries.length === 0}
              data-testid="button-delete-all-pantries"
            >
              <Trash2 className="h-4 w-4 mr-2" />
              Delete All ({Array.isArray(pantries) ? pantries.length : 0})
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          {!pantries || !Array.isArray(pantries) || pantries.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              <p>No pantries found. Upload a CSV file to get started.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {Array.isArray(pantries) && pantries.map((pantry: any) => (
                <div key={pantry.id} className="flex items-center justify-between p-4 border rounded-lg" data-testid={`pantry-item-${pantry.id}`}>
                  <div className="flex-1">
                    <h3 className="font-semibold" data-testid={`text-pantry-name-${pantry.id}`}>{pantry.name}</h3>
                    <p className="text-sm text-muted-foreground">
                      {pantry.address}, {pantry.city}, {pantry.state} {pantry.zipCode}
                    </p>
                    {pantry.phone && (
                      <p className="text-sm text-muted-foreground">{pantry.phone}</p>
                    )}
                    {pantry.hours && (
                      <p className="text-sm text-muted-foreground">Hours: {pantry.hours}</p>
                    )}
                  </div>
                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setEditingPantry(pantry.id)}
                      data-testid={`button-edit-${pantry.id}`}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="destructive"
                      size="sm"
                      onClick={() => deletePantryMutation.mutate(pantry.id)}
                      disabled={deletePantryMutation.isPending}
                      data-testid={`button-delete-${pantry.id}`}
                    >
                      {deletePantryMutation.isPending ? (
                        <RefreshCw className="h-4 w-4 animate-spin" />
                      ) : (
                        <Trash2 className="h-4 w-4" />
                      )}
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Delete All Confirmation Dialog */}
      {showDeleteAll && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg max-w-md mx-4">
            <h3 className="text-lg font-semibold mb-4">Delete All Pantries</h3>
            <p className="text-muted-foreground mb-6">
              Are you sure you want to delete all {Array.isArray(pantries) ? pantries.length : 0} pantries? This action cannot be undone.
            </p>
            <div className="flex space-x-4">
              <Button
                variant="destructive"
                onClick={() => deleteAllMutation.mutate()}
                disabled={deleteAllMutation.isPending}
                data-testid="button-confirm-delete-all"
              >
                {deleteAllMutation.isPending ? (
                  <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                ) : (
                  <Trash2 className="h-4 w-4 mr-2" />
                )}
                Delete All
              </Button>
              <Button
                variant="outline"
                onClick={() => setShowDeleteAll(false)}
                disabled={deleteAllMutation.isPending}
                data-testid="button-cancel-delete-all"
              >
                Cancel
              </Button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Pantry Modal placeholder */}
      {editingPantry && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg max-w-2xl mx-4 w-full">
            <h3 className="text-lg font-semibold mb-4">Edit Pantry</h3>
            <p className="text-muted-foreground mb-4">
              Edit functionality coming soon! For now, you can delete and re-import pantries.
            </p>
            <Button onClick={() => setEditingPantry(null)} data-testid="button-close-edit">
              Close
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/layout/header.tsx.content:
import { useState } from "react";
import { MapPin, List, Settings, LogOut, Menu, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useAuth } from "@/hooks/useAuth";
import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";

interface HeaderProps {
  onToggleView?: () => void;
  currentView?: 'map' | 'list';
}

export default function Header({ onToggleView, currentView = 'map' }: HeaderProps) {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const { isAuthenticated } = useAuth();

  // Fetch admin settings for logo and organization name
  const { data: adminSettings } = useQuery({
    queryKey: ['/api/admin/settings'],
  });

  const handleLogout = () => {
    window.location.href = '/api/logout';
  };

  return (
    <header className="bg-card border-b border-border shadow-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <Link href="/" className="flex items-center space-x-4 hover:opacity-80 transition-opacity">
            {adminSettings?.logoUrl ? (
              <img 
                src={adminSettings.logoUrl} 
                alt="Organization Logo"
                className="h-10 w-auto object-contain"
                onError={(e) => {
                  // Fallback to icon if image fails to load
                  e.currentTarget.style.display = 'none';
                  e.currentTarget.nextElementSibling?.classList.remove('hidden');
                }}
              />
            ) : null}
            <MapPin className={`text-primary text-2xl ${adminSettings?.logoUrl ? 'hidden' : ''}`} />
            <div>
              <h1 className="text-xl font-semibold text-foreground" data-testid="text-app-title">
                Find a Pantry
              </h1>
              <p className="text-sm text-muted-foreground" data-testid="text-org-name">
                {adminSettings?.organizationName || "Second Harvest Food Bank of the Lehigh Valley"}
              </p>
            </div>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-4">
            {onToggleView && (
              <Button
                variant="ghost"
                onClick={onToggleView}
                className="flex items-center space-x-2"
                data-testid="button-toggle-view"
              >
                <List className="h-4 w-4" />
                <span>{currentView === 'map' ? 'List View' : 'Map View'}</span>
              </Button>
            )}

            {isAuthenticated ? (
              <>
                <Button asChild variant="ghost" data-testid="button-admin-dashboard">
                  <Link href="/admin" className="flex items-center space-x-2">
                    <Settings className="h-4 w-4" />
                    <span>Admin</span>
                  </Link>
                </Button>
                <Button
                  variant="ghost"
                  onClick={handleLogout}
                  className="flex items-center space-x-2"
                  data-testid="button-logout"
                >
                  <LogOut className="h-4 w-4" />
                  <span>Logout</span>
                </Button>
              </>
            ) : (
              <Button
                onClick={() => window.location.href = '/api/login'}
                className="bg-primary text-primary-foreground hover:bg-primary/90"
                data-testid="button-login"
              >
                <Settings className="h-4 w-4 mr-2" />
                Admin Login
              </Button>
            )}
          </div>

          {/* Mobile Menu Button */}
          <Button
            variant="ghost"
            size="sm"
            className="md:hidden"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            data-testid="button-mobile-menu"
          >
            {isMobileMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
          </Button>
        </div>

        {/* Mobile Menu */}
        {isMobileMenuOpen && (
          <div className="md:hidden border-t border-border py-4">
            <div className="space-y-2">
              {onToggleView && (
                <Button
                  variant="ghost"
                  onClick={() => {
                    onToggleView();
                    setIsMobileMenuOpen(false);
                  }}
                  className="w-full justify-start"
                  data-testid="button-mobile-toggle-view"
                >
                  <List className="h-4 w-4 mr-2" />
                  {currentView === 'map' ? 'List View' : 'Map View'}
                </Button>
              )}

              {isAuthenticated ? (
                <>
                  <Button asChild variant="ghost" className="w-full justify-start" data-testid="button-mobile-admin">
                    <Link href="/admin" onClick={() => setIsMobileMenuOpen(false)}>
                      <Settings className="h-4 w-4 mr-2" />
                      Admin Dashboard
                    </Link>
                  </Button>
                  <Button
                    variant="ghost"
                    onClick={() => {
                      handleLogout();
                      setIsMobileMenuOpen(false);
                    }}
                    className="w-full justify-start"
                    data-testid="button-mobile-logout"
                  >
                    <LogOut className="h-4 w-4 mr-2" />
                    Logout
                  </Button>
                </>
              ) : (
                <Button
                  onClick={() => {
                    window.location.href = '/api/login';
                    setIsMobileMenuOpen(false);
                  }}
                  className="w-full justify-start bg-primary text-primary-foreground hover:bg-primary/90"
                  data-testid="button-mobile-login"
                >
                  <Settings className="h-4 w-4 mr-2" />
                  Admin Login
                </Button>
              )}
            </div>
          </div>
        )}
      </div>
    </header>
  );
}

client/src/components/pantry/pantry-card.content:
import { Phone, Navigation, MapPin } from "lucide-react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Pantry } from "@shared/schema";

interface PantryCardProps {
  pantry: Pantry;
  onClick: () => void;
  onShowOnMap?: () => void;
  distance?: number;
  isSelected?: boolean;
}

export default function PantryCard({ pantry, onClick, onShowOnMap, distance, isSelected }: PantryCardProps) {
  const getAccessTypeBadge = (accessType: string | null) => {
    switch (accessType) {
      case 'walk-in':
        return <Badge variant="default" className="bg-primary/10 text-primary">Walk-in</Badge>;
      case 'appointment':
        return <Badge variant="secondary" className="bg-secondary/10 text-secondary">By Appointment</Badge>;
      case 'mobile':
        return <Badge variant="outline" className="border-accent text-accent-foreground">Mobile</Badge>;
      default:
        return <Badge variant="outline">Contact for Info</Badge>;
    }
  };

  const handleDirections = (e: React.MouseEvent) => {
    e.stopPropagation();
    const address = `${pantry.address}, ${pantry.city}, ${pantry.state} ${pantry.zipCode}`;
    const encodedAddress = encodeURIComponent(address);
    window.open(`https://www.google.com/maps/dir/?api=1&destination=${encodedAddress}`, '_blank');
  };

  const handleCall = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (pantry.phone) {
      window.location.href = `tel:${pantry.phone}`;
    }
  };

  const handleShowOnMap = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (onShowOnMap) {
      onShowOnMap();
    }
  };

  return (
    <Card 
      className={`p-4 hover:shadow-md transition-shadow cursor-pointer hover:bg-accent/5 ${isSelected ? 'ring-2 ring-primary bg-primary/5' : ''}`}
      onClick={onClick}
      data-testid={`card-pantry-${pantry.id}`}
    >
      <div className="flex justify-between items-start mb-2">
        <h3 className="font-semibold text-foreground line-clamp-2 flex-1 mr-2" data-testid={`text-pantry-name-${pantry.id}`}>
          {pantry.name}
        </h3>
        {distance !== undefined && (
          <span className="text-xs bg-accent text-accent-foreground px-2 py-1 rounded-full whitespace-nowrap">
            {distance.toFixed(1)} mi
          </span>
        )}
      </div>

      <p className="text-sm text-muted-foreground mb-2" data-testid={`text-pantry-address-${pantry.id}`}>
        {pantry.address}<br />
        {pantry.city}, {pantry.state} {pantry.zipCode}
      </p>

      {pantry.phone && (
        <div className="flex items-center text-sm text-muted-foreground mb-2">
          <Phone className="h-4 w-4 mr-2" />
          <span data-testid={`text-pantry-phone-${pantry.id}`}>{pantry.phone}</span>
        </div>
      )}

      {pantry.hours && (
        <p className="text-sm text-muted-foreground mb-3" data-testid={`text-pantry-hours-${pantry.id}`}>
          {pantry.hours}
        </p>
      )}

      <div className="flex flex-col gap-3">
        <div className="flex items-center justify-between">
          {getAccessTypeBadge(pantry.accessType)}
        </div>
        <div className="flex gap-1 flex-wrap">
          {onShowOnMap && (
            <Button
              variant="ghost"
              size="sm"
              onClick={handleShowOnMap}
              className="text-primary hover:text-primary/80 text-xs px-2 py-1 h-auto"
              data-testid={`button-show-on-map-${pantry.id}`}
            >
              <MapPin className="h-3 w-3 mr-1" />
              Show on Map
            </Button>
          )}
          {pantry.phone && (
            <Button
              variant="ghost"
              size="sm"
              onClick={handleCall}
              className="text-primary hover:text-primary/80 text-xs px-2 py-1 h-auto"
              data-testid={`button-call-${pantry.id}`}
            >
              <Phone className="h-3 w-3 mr-1" />
              Call
            </Button>
          )}
          <Button
            variant="ghost"
            size="sm"
            onClick={handleDirections}
            className="text-primary hover:text-primary/80 text-xs px-2 py-1 h-auto"
            data-testid={`button-directions-${pantry.id}`}
          >
            <Navigation className="h-3 w-3 mr-1" />
            Directions
          </Button>
        </div>
      </div>
    </Card>
  );
}

